<script type="text/javascript">
  //获取第一级别数据
  $.ajax({
    //设置参数
    'url':'/iorder/csjl',//服务器端页面
    'type':'get',//请求方式 等下 老铁 
    'async':false,//请求处理方式
    'data':{upid:0},//附加参数
    'dataType':'json',//响应数据返回类型
    //Ajax 响应成功
    success:
    //data 响应数据
    function(data){
      // alert(data);
      for(var i=0;i<data.length;i++){
        // alert(data[i].name);
        //把遍历的第一级别数据存放在option
        op='<option value="'+data[i].id+'" id="qxz">'+data[i].name+'</option>';
        // alert(op);
        //把带有数据的option 内部插入到select
        $("#cid").append(op);
      }
    },
    error:
    //Ajax 响应失败
    function(){
      alert("Ajax 请求失败");
    }
  });

  //获取其他级别数据
  // onchange 
  //live 绑定事件和事件处理器函数
  $("select").live('change',function(){
    //获取id=》当做下一个子级upid
    ob=$(this).val();
    obs=$(this);
    //清除当前元素之后的所有同辈的select
    // nextAll 获取当前元素之后的所有同辈元素
    obs.nextAll("select").remove();
    //Ajax
    $.ajax({
      'url':'/iorder/csjl',
      'type':'get',
      'async':false,
      'data':{upid:ob},
      'dataType':'json',
      success:
      function(data){
        //创建select
        // document.createElement()
        select=$("<select></select>");
        //内部插入option
        select.append("<option value='' id='qxz'>--请选择--</option>");
        // alert(data);
        //判断
        if(data.length>0){
          //遍历
          for(var i=0;i<data.length;i++){
            // alert(data[i].name);
            //把当前遍历的级别数据存放在option
            op='<option value="'+data[i].id+'">'+data[i].name+'</option>';
            // alert(op);
            //把带有数据的option插入到创建好的select
            select.append(op);

          }
          //把select 追加到上一个selelct后
          obs.after(select);
        }
      },
      error:
      function(){
        alert('Ajax响应失败');
      }
    });
  });

  //获取选中的数据
  arr=[];
